import React, { useState, useEffect } from "react";
// Configuração para requisições na rede
import web3 from "./web3";
// Informação do contrato
import loteria from "./loteria";

const App = () => {
  // Cria variáveis e funções de alteração
  const [gerente, setGerente] = useState("");
  const [jogadores, setJogadores] = useState("");
  const [saldo, setSaldo] = useState("");
  const [value, setValue] = useState("");
  const [mensagem, setMensagem] = useState("");
  //Alterei aqui
  const [vencedor, setWinner] = useState("");
  const [saldoVencedor, setSaldoVencedor] = useState("");
  // Função assincrona que carrega os dados do contrato
  const carregarDados = async () => {
    
    // Pega a carteira do gerente do contrato
    const _gerente = await loteria.methods.gerente().call();
    // Pega a carteira dos jogadores
    const _jogadores = await loteria.methods.getJogadores().call();
    // Pega o valor total vinculado ao contrato
    const _saldo = await web3.eth.getBalance(loteria.options.address);
    //Alterado aqui
    const _vencedor = await loteria.methods.vencedor().call();
    const _saldoVencedor = await loteria.methods.saldo().call();
    console.log(_vencedor);
    console.log(_saldoVencedor);

    // Armazena os valores nas variáveis de gerente, jogador e saldo
    setWinner(_vencedor);
    setSaldoVencedor(_saldoVencedor);
    setGerente(_gerente);
    setJogadores(_jogadores);
    setSaldo(_saldo);
    setValue("");
    
  };
  // Antes da página carregar ele chama seu conteúdo
  useEffect(() => {
    // Busca dados do contrato
    carregarDados();
  }, []);

  // * Realiza uma aposta
  const apostar = async (event) => {
    try {
      // Evita que a página seja recarregada
      event.preventDefault();
      // Altera valor da mensagem exibida
      setMensagem("Aguardando a  validação da transação...");
      // Pega contas do metamask
      const contas = await web3.eth.getAccounts();
      // console.log(contas);

      // Joga passando valor da conta principal e o valor de ether em wei
      await loteria.methods.jogar().send({
        from: contas[0],
        value: web3.utils.toWei(value, "ether"),
      });
      // Recarrega dados da página
      await carregarDados();
      // Altera mensagem
      setMensagem("Transação concluida!");
    } catch (error) {
      // Caso o usuário cancele a solicitação no metamask
      if (error.code === 4001) {
        setMensagem("Transação cancelada!");
      } else {
        // Caso algo esteja fora das políticas do contrato
        setMensagem("Transação vai contra regras do contrato");
      }
    }
  };
  // * Realiza sorteio
  const sortear = async () => {
    try {
      // Altera mensagem
      setMensagem("Aguardando processamento...");
      // Pega contas do metamask
      const contas = await web3.eth.getAccounts();
      // Solicita sorte e manda conta que está realizando o sorteio
      await loteria.methods.sorteio().send({
        from: contas[0],
      });
      // Recarrega dados da página
     // await carregarDados();
      // Altera mensagem
      //setMensagem(`Um vencedor foi escolhido!  ${vencedor}  ${saldoVencedor}`);
       //setMensagem("Um vencedor foi escolhido! ");
       await carregarDados();
       setMensagem(`Um vencedor foi escolhido!  ${vencedor}`);
    } catch (error) {
      // Caso o usuário cancele a solicitação no metamask
      if (error.code === 4001) {
        setMensagem("Transação cancelada!");
      } else {
        // Caso algo esteja fora das políticas do contrato
        setMensagem("Transação vai contra regras do contrato");
      }
    }
  };

  return (
    <div className="divPrincipal">

      <div className="divTexto">
         <h2 className="titulo">Painel de Controle</h2>

      </div>
     
      <p className="texto">
        Neste painel você tem acesso as principais funções do SWM.
      </p>
      <br />

        <div className="grid">
        <button className="b1">Nível de água</button>   <br />   <br />
        <button className="b2">Ligar Bomba</button>   <br />   <br />
        <button className="b3">Desligar Bomba</button>   <br />  <br />
        <button className="b4">Abrir valvula</button>   <br />  <br />
        <button className="b5">Fechar valvula</button>   <br />  <br />
        <button className="b6">Abrir Painel</button>   <br />  <br />
        <button className="b7">Atualizar Painel</button>   <br />  <br />

        </div>

      
    </div>
  );
};

export default App;
